mapper.insert:
INSERT INTO member (

) VALUES (
	  #{user_id}
	, #{password}
	, #{zip_code}
	, #{address}
	, #{address_detail}
	, NOW()
	, NULL
)

mapper.update:
UPDATE 
	member
SET
	  update_time = NOW()
	, zip_code = #{zip_code}
	, address = #{address}
	, address_detail = #{address_detail}
WHERE
	user_id = #{user_id}

UPDATE 
	member
SET 
	password = #{password}
WHERE 
	user_id = #{user_id}
